import React, { useState } from "react";
import { Row, Col } from "react-bootstrap";
import "./Home.css"
import Navbar from "./Navbar/Navbar";
import Footer from "./footer/Footer";
export const Home = () => {
    const email = "email"
    const [file,setFile]=useState("")
    const [fileContent, setFileContent] = useState(""); // State to store the content of the file

    // Function to handle file change
    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);

        if (selectedFile) {
            const reader = new FileReader();

            reader.onload = (event) => {
                // Access the file content using event.target.result
                const content = event.target.result;
                console.log("File content:", content); // Log the content to the console
                setFileContent(content);
            };

            // Read the file as text
            reader.readAsText(selectedFile);
        }
    };


    const [que,setQue]=useState("")

    const [queArr,setQueArr] = useState([
        "Who are you?",
        "Who are you?",
        "Who are you?",
        "Who are you?",
    ])
    const [ansArr,setAnsArr]=useState([
        "Let me know!",
        "Let me know!",
        "Let me know!",
        "Let me know!",
        "Let me know!",
        "Let me know!",
        "Let me know!",
        "Let me know!",
        "Let me know!",
        "Let me know!",
        "Let me know!",

    ])


    const handleQuestions = (text) => {
        setQueArr((prev)=>[...prev,text])
        setQue("")
    }

    return (
        <div className="home_container">
            <Navbar />
            <div className="home_content">
                <div>
                    <div style={{ display: 'flex', padding: '20px', justifyContent: 'center' }}>
                        <form name="foo" method="post" encType="multipart/form-data">
                            <input type="file" accept="application/pdf"
                                // console.log(e.target.value)
                                onChange={handleFileChange} />
                        </form>
                        <span>
                            <button>Close</button>
                        </span>
                    </div>
                    <div>
                        <Row>
                            <Col md={6} lg={6} className="px-3">
                                <div className="card-content">
                                    <div className="text">
                                        {
                                            queArr.map((item, idx) => {
                                                return (
                                                    ansArr[idx] && <div key={idx} style={{ display: 'flex', flexDirection: 'column' }}>
                                                        <span id="questions">{item} </span>
                                                        <span id="answers">{ansArr[idx]} </span>
                                                    </div>
                                                )
                                            })
                                        }
                                    </div>
                                    <div style={{display:'flex',alignItems:'center'}}>
                                        <input className="input_text" type="text" value={que} onChange={(e)=>{
                                            setQue(e.target.value)
                                        }}></input>
                                        <i style={{fontSize:'25px',cursor:'pointer'}} className="fa-solid fa-right-to-bracket px-3" onClick={()=>handleQuestions(que)}></i>
                                    </div>
                                </div>
                            </Col>
                            <Col md={6} lg={6} className="px-3">
                                <div className="card-content">

                                </div>

                            </Col>
                        </Row>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    )
}
