  return (
    <div className="border-shadow header col-md-12 py-2 header">
      <div className="responsive-width px-4">
        <Row className="responsive-md resposive-lg m-0 p-0">
          <Col xs sm={6} md={6} lg={6} className="cursor-pointer d-flex align-items-center p-0">
