import React, { useState } from "react";
import { Row, Col } from "react-bootstrap";
import "./Home.css"
import Navbar from "./Navbar/Navbar";
import Footer from "./footer/Footer";
import pdfToText from 'react-pdftotext'
import axios from "axios";



export const Home = () => {
    const email = "email"
    const [file, setFile] = useState("")
    const [fileContent, setFileContent] = useState(""); // State to store the content of the file
    const [fileText, setFileText] = useState("")

    const [que1, setQue1] = useState("")
    const [que2, setQue2] = useState("")

    const [queArr1, setQueArr1] = useState([])
    const [ansArr1, setAnsArr1] = useState([])
    const [text1, setText1] = useState("")


    const [queArr2, setQueArr2] = useState([])
    const [ansArr2, setAnsArr2] = useState([])
    const [text2, setText2] = useState("")

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);

        if (selectedFile) {
            pdfToText(selectedFile)
                .then(text => {setFileText(text)
                console.log(text,'file text')})
                .catch(error => console.error("Failed to extract text from pdf"))
        }
    };

    const handleUploadPdf = () => {
        const url = `http://0.0.0.0:8000/getsummary/${fileText}`;
        console.log(fileText,'filetext')

        axios.get(url)
            .then(response => {
                console.log('Response:', response.data.Summary1);
                setText1(response.data.Summary1)
                setText2(response.data.Summary2)

            })
            .catch(error => {
                console.error('Error:', error);
            });
    };


    const handleQuestions1 = (text) => {
        setQueArr1((prev) => [...prev, text])
        const url = `http://0.0.0.0:8000/getsummary1/${text}`;

        axios.get(url)
            .then(response => {
                console.log('Response:', response.data);
                setAnsArr1((prev) => [...prev, response.data.Summary1])

            })
            .catch(error => {
                console.error('Error:', error);
            });

        setQue1("")
    }

    const handleQuestions2 = (text) => {
        setQueArr2((prev) => [...prev, text])
        const url = `http://0.0.0.0:8000/getsummary2/${text}`;

        axios.get(url)
            .then(response => {
                console.log('Response:', response.data);
                setAnsArr2((prev) => [...prev, response.data.Summary2])

            })
            .catch(error => {
                console.error('Error:', error);
            });

        setQue2("")
    }

    return (
        <div className="home_container">
            <Navbar handleUploadPdf={handleUploadPdf} />
            <div className="home_content">
                <div>
                    <div style={{ display: 'flex', padding: '20px', justifyContent: 'center' }}>
                        <form name="foo" method="post" encType="multipart/form-data">
                            <input type="file" accept="application/pdf"
                                // console.log(e.target.value)
                                onChange={handleFileChange} />
                        </form>
                        <span>
                            <button>Close</button>
                        </span>
                    </div>
                    <div>
                        <Row>
                            <Col md={6} lg={6} className="px-3">
                                <div className="card-content">
                                    <div className="text">
                                        {text1 && <span>{text1}</span>}
                                        {
                                            queArr1.map((item, idx) => {
                                                return (
                                                    ansArr1[idx] && <div key={idx} style={{ display: 'flex', flexDirection: 'column' }}>
                                                        <span id="questions">{item} </span>
                                                        <span id="answers">{ansArr1[idx]} </span>
                                                    </div>
                                                )
                                            })
                                        }
                                    </div>
                                    <div style={{ display: 'flex', alignItems: 'center' }}>
                                        <input className="input_text" type="text" value={que1} onChange={(e) => {
                                            setQue1(e.target.value)
                                        }}></input>
                                        <i style={{ fontSize: '25px', cursor: 'pointer' }} onClick={() => handleQuestions1(que1)} className="bi bi-box-arrow-in-up"></i>
                                        {/* <i className="fa-solid fa-right-to-bracket px-3" onClick={() => handleQuestions1(que1)}></i> */}
                                    </div>
                                </div>
                            </Col>
                            <Col md={6} lg={6} className="px-3">
                            <div className="card-content">
                                    <div className="text">
                                        {text2 && <span>{text2}</span>}
                                        {
                                            queArr2.map((item, idx) => {
                                                return (
                                                    ansArr2[idx] && <div key={idx} style={{ display: 'flex', flexDirection: 'column' }}>
                                                        <span id="questions">{item} </span>
                                                        <span id="answers">{ansArr2[idx]} </span>
                                                    </div>
                                                )
                                            })
                                        }
                                    </div>
                                    <div style={{ display: 'flex', alignItems: 'center' }}>
                                        <input className="input_text" type="text" value={que2} onChange={(e) => {
                                            setQue2(e.target.value)
                                        }}></input>
                                        <i style={{ fontSize: '25px', cursor: 'pointer' }} className="fa-solid fa-right-to-bracket px-3" onClick={() => handleQuestions2(que2)}></i>
                                    </div>
                                </div>

                            </Col>
                        </Row>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    )
}
